    var toReturn = {};

    var $content = $(this);
    var $fullScreenWrap = $('<div id="dwf-fullscreen-wrap" />');
    var $exodusSource = $('<div id="dfs-exodus-source" />');
    var $closeButton = $("<a href='#' class='dfs-close-button'>" + set.closeText + "</a>");
